UPDATE page_sections SET content = '{
  "title": "Switch and Outlet Repair: Tips for Homeowners",
  "tips": [
    "**Before: Identify Symptoms & Turn Off Power:** Clearly describe the issue (e.g., ''outlet sparking,'' ''switch not working intermittently'') and always turn off the corresponding breaker before any personal inspection or before the professional arrives. This ensures safety and helps the pro diagnose faster.",
